        public static byte[] addBytes(byte[] data1, byte[] data2)
        {
            byte[] data3 = new byte[data1.Length + data2.Length];
            Array.Copy(data1, 0, data3, 0, data1.Length);
            Array.Copy(data2, 0, data3, data1.Length, data2.Length);
            return data3;
        }
